import React from "react";
class Counter extends React.Component{
    constructor()
    {
        super()
        this.state={
            count:0
        }
    }
    increment()
    {
        this.setState({
            count:this.state.count+1
        })
    }
    render()
    {
        return(
            <>
            <div>Count {this.state.count}</div>
            <button onClick={this.increment()}>click</button>
           </>
        )
       
       
    }
}
export default Counter
